import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class HomePage extends Component {
  state = {
    users: null,
    user_name: null,
    user: null,
    redirect: false
  }

  componentDidMount() {
    UsersAPI.fetchUsers()
    .then(json => this.setState({
      users: json
    }))
  }

  checkName = () => {
    let holder = 0;
    return this.state.users.map(user => {
      if(user.user_name === this.state.user_name) {
        holder = -1;
        if(holder === -1) {
          return this.setState({
            user: user,
            redirect: true 
          })
        } else if(holder === this.state.users.length && holder !== -1) {
          return <Redirect to={`/babynames/`} />
        }
      } else {
        holder += 1;        
      }
    })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.checkName();
  }

  handleUserName = (e) => {
    this.setState({user_name: e.target.value});
  }

  render() {
    if(this.state.redirect) {
    return <Redirect to={`/babynames/${this.state.user.id}`} />
    }

    return (
      <div>
        <form>
          <h6>Enter User Name:</h6>
          <input type='text' value={this.state.user} onChange={this.handleUserName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
      </div>
    )
  }
}

export default HomePage;
