import React, { Component } from 'react';
import ListsAPI from '../../api/ListsAPI';
import NamesAPI from '../../api/NamesAPI';
import '../../App.css';
import '../../pages/ListNamesPage.css'

class NameForm extends Component {
  state = {
    names: null,
    baby_name: '',
    redirect: null,
    name_params: null
  }

  componentDidMount() {
    return this.props.lists.map(list => {
      if(list.list_name === this.props.listID)
        return ListsAPI.fetchListByID(list.id)
        .then(json => this.setState({ list: json }))
    })
  }

  checkBabyName = () => {
    if(this.props.names.length === 0) {
      this.props.handleNameSave();
    } else {
      let counter = 0;
      while(counter < this.props.names.length) {
        if(this.props.names[counter].baby_name.toUpperCase() === this.props.babyName.toUpperCase()) {
          this.props.saveBabyName();
          break;
        } else if(counter === this.props.names.length - 1) {
          this.props.handleNameSave();
        } 
        counter++;
      }
    }
  }

  handleBabyName = (e) => {
    this.props.handleBabyName(e);
  }

  handleSubmit = (e) => {
    e.preventDefault();
    if(/[0-9]+/gi.test(this.props.babyName) === true) {
      return this.props.voidBabyName();
    }
    this.checkBabyName();
  }

  render() {
    return (
      <div>

        <form>
          <h5>Add Baby Name:</h5>
          <input type='text' value={this.props.babyName} onChange={this.handleBabyName} />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
      </div>
    )
  }
}

export default NameForm;
