import React, { Component } from 'react';
import { BrowserRouter, Route } from 'react-router-dom';
import ListsAPI from './api/ListsAPI';

// import NavBar from './components/NavBar/NavBar';
import HomePage from './pages/HomePage';
// import UserPage from './pages/UserPage';
import ListPage from './pages/ListPage';
// import ListNamesPage from './pages/ListNamesPage';
import './App.css';

class App extends Component {
  state = {
    lists: ListsAPI.fetchLists(),
    listName: null,
    listID: null
  }

  // startListsState = () => {
  //   ListsAPI.fetchLists()
  //   .then(json => this.setState({ lists: json }))
  // }

  listsState = (lists) => {
    this.setState({ lists: lists })
  }

  listNameState = (listName) => {
    this.setState({ listName: listName })
  }

  listIdState = (listName) => {
    return this.state.lists.map(list => {
      if(list.list_name === this.state.listName) {
        this.setState({ listID: list.id })
      }
    })
  }

  render() {
    return (
      <BrowserRouter>
        <div className='App'>
          {/* <NavBar /> */}
          <Route exact path='/' render={() => <HomePage startListsState={this.startListsState()} listsState={this.listsState} listNameState={this.listNameState} lists={this.state.lists} listName={this.state.listName}/>} />
          <Route exact path='/:listID' render={() => <ListPage listIdState={this.listIdState(this.state.listName)} lists={this.state.lists} listName={this.state.listName} listID={this.state.listID} />} />
          {/* <Route exact path='/babynames/:userID' component={UserPage} /> */}
          {/* <Route exact path='/babynames/:userID/lists' component={ListNamesPage} /> */}
          {/* <Route exact path='/babynames/:userID/lists/:listID' component={ListPage} /> */}
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
