import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import ListsAPI from '../api/ListsAPI';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class UserPage extends Component {
  state = {
    lists: null,
    user: null,
    list_teaser: null,
    list: null,
    list_name: '',
    redirect: null
  }

  componentDidMount() {
    ListsAPI.fetchLists()
    .then(json => this.setState({
      lists: json
    }))
    UsersAPI.fetchUserByID(this.props.match.params.userID)
    .then(json => this.setState({
      user: json
    }))
  }

  checkListName = () => {
    let counter = 0;
    while(counter < this.state.lists.length) {
      if(this.state.lists[counter].list_name === this.state.list_name) {
        this.setState({ 
          list_name: '',
          redirect: false
        })
        break;
      } else if(counter === this.state.lists.length - 1) {
        ListsAPI.addList({list_name: this.state.list_name})
        .then(() => ListsAPI.fetchLists())
        .then(json => 
          this.setState({
            lists: json,
            redirect: true
        }))
      } 
      counter++;
    }
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.checkListName();
  }

  handleListName = (e) => {
    this.setState({list_name: e.target.value});
  }

  handleRecentlyViewedList = () => {
    ListsAPI.fetchListByID(this.state.user.recently_viewed_list)
    .then(json => this.setState({
      list_teaser: json
    }))
  }

  render() {
    if(this.state.user) {
      this.handleRecentlyViewedList();
    }
    if(this.state.redirect) {
      return this.state.lists.map((list, index) => {
        if(list.list_name === this.state.list_name) {
          return <Redirect key={index} to={`/babynames/${this.props.match.params.userID}/${list.id}`} />
        }
      })
    }

    return (
      <div>
        {/* {this.state.list_teaser ? <p>{this.state.list_teaser</p>} */}
        <form>
          <h6>Create New Baby Name List:</h6>
          {this.state.redirect === false ? <p>That list name is taken. Please try another name.</p>: null}
          <input type='text' value={this.state.list_name} onChange={this.handleListName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
      </div>
    )
  }
}

export default UserPage;
