import React, { Component } from 'react';
import NamesDisplay from '../NamesDisplay/NamesDisplay';
import ListsAPI from '../../api/ListsAPI';
import NamesAPI from '../../api/NamesAPI';
import '../../App.css';
import '../../pages/ListNamesPage.css'

class NameForm extends Component {
  state = {
    names: null,
    baby_name: '',
    redirect: null,
    name_params: null
  }

  componentDidMount() {
    return NamesAPI.fetchNamesByListID(this.props.list.id)
      .then(json => this.setState({ names: json }))
  }

  handleNameSave = () => {
    NamesAPI.addListName(this.props.list.id, {baby_name: this.state.baby_name})
    .then(() => NamesAPI.fetchNamesByListID(this.props.list.id))
    .then(json => {
      this.setState({
        names: json,
        baby_name: '',
        redirect: null,
        name_params: null,
    })})
  }

  checkBabyName = () => {
    if(this.state.names.length === 0) {
      this.handleNameSave();
    } else {
      let counter = 0;
      while(counter < this.state.names.length) {
        if(this.state.names[counter].baby_name.toUpperCase() === this.state.baby_name.toUpperCase()) {
          this.setState({
            baby_name: '',
            redirect: true
          })
          break;
        } else if(counter === this.state.names.length - 1) {
          this.handleNameSave();
        } 
        counter++;
      }
    }
  }

  handleBabyName = (e) => {
    this.setState({baby_name: e.target.value});
  }

  handleSubmit = (e) => {
    e.preventDefault();
    if(/[0-9]+/gi.test(this.state.baby_name) === true) {
      return this.setState({
        baby_name: '',
        name_params: false
      });
    }
    this.checkBabyName();
  }

  displayNames = () => {
    return this.state.names.map((name, index) => {
      return (
        <div key={index} className='baby-name'>{name.baby_name}</div>
      )
    })
  }

  handleSort = (namesHolder) => {
    let sortHolder = namesHolder.sort((a, b) => {
    let A = a.baby_name;
    let B = b.baby_name;
    return (A < B) ? -1: (A > B) ? 1: 0;
    });
    return sortHolder.map((name, index) => {
      console.log(name)
      return (
        <div key={index} className='baby-name'>
          {name.toLowerCase()}<br/>
        </div>
      )
    })
  }

  sortNames = () => {
    let names = [];
    this.props.names.map(name => {
      names.push(name.baby_name.toUpperCase());
    })
    this.handleSort(names);
  }

  render() {
    return (
      <div>

        <form>
          <h5>Add Baby Name:</h5>
          <input type='text' value={this.state.baby_name} onChange={this.handleBabyName} />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
          { this.state.name_params === false ? <h6>Please do not include numbers in your names.</h6>: null }
          { this.state.redirect === true ? <h6>Name already included in list.</h6>: null }
        </form>

        <div className='baby-names'>
          { this.state.names ? this.displayNames(): null}
          { this.state.names ? this.sortNames(): null}
        </div>
      </div>
    )
  }
}

export default NameForm;
