import React, { Component } from 'react';
import NamesDisplay from '../NamesDisplay/NamesDisplay';
import NameForm from '../NameForm/NameForm';
import ListsAPI from '../../api/ListsAPI';
import NamesAPI from '../../api/NamesAPI';
import '../../App.css';
import '../../pages/ListNamesPage.css'

class ListNamesPage extends Component {
  state = {
    list: null,
    names: null,
    baby_name: '',
    redirect: null,
    name_params: null
  }

  componentDidMount() {
    return this.props.lists.map(list => {
      if(list.list_name === this.props.listID)
      ListsAPI.fetchListByID(list.id)
      .then(json => this.setState({ list: json }))
      NamesAPI.fetchNamesByListID(list.id)
      .then(json => this.setState({ names: json }))
    })
  } 

  handleNameSave = () => {
    NamesAPI.addListName(this.state.list.id, {baby_name: this.state.baby_name})
    .then(() => NamesAPI.fetchNamesByListID(this.state.list.id))
    .then(json => {
      this.setState({
        names: json,
        baby_name: '',
        redirect: null,
        name_params: null,
    })})
  }

  saveBabyName = () => {
    this.setState({
      baby_name: '',
      redirect: true
    })
  }

  checkBabyName = () => {
    if(this.state.names.length === 0) {
      this.handleNameSave();
    } else {
      let counter = 0;
      while(counter < this.state.names.length) {
        if(this.state.names[counter].baby_name.toUpperCase() === this.state.baby_name.toUpperCase()) {
          this.setState({ 
            baby_name: '',
            redirect: true
          })
          break;
        } else if(counter === this.state.names.length - 1) {
          this.handleNameSave();
        } 
        counter++;
      }
    }
  }

  handleBabyName = (e) => {
    this.setState({baby_name: e.target.value});
  }

  handleSubmit = (e) => {
    e.preventDefault();
    if(/[0-9]+/gi.test(this.state.baby_name) === true) {
      return this.setState({
        baby_name: '',
        name_params: false
      })
    }
    this.checkBabyName();
  }

  render() {
    return (
      <div>
        { this.state.list ? <h1>*{this.state.list.list_name}*</h1>: null }

        {/* <form>
          <h5>Add Baby Name:</h5>
          <input type='text' value={this.state.baby_name} onChange={this.handleBabyName} />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
          { this.state.name_params === false ? <h6>Please do not include numbers in your names.</h6>: null }
          { this.state.redirect === true ? <h6>Name already included in list.</h6>: null }
        </form> */}
        <div>
          <NameForm handleNameSave={this.handleNameSave} saveBabyName={this.saveBabyName}/>
        </div>

        <div className='baby-names'>
          { this.state.names ? <NamesDisplay names={this.state.names} />: this.state.redirect ? <NamesDisplay names={this.state.names} />: null}
        </div> 
      </div>
    )
  }
}

export default ListNamesPage;
