import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import ListsAPI from '../api/ListsAPI';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class HomePage extends Component {
  state = {
    users: null,
    user_name: null,
    redirect: false
    // list_name: ''
  }

  componentDidMount() {
    UsersAPI.fetchUsers()
    .then(json => this.setState({
      users: json
    }))
  }
  // handleSubmit = (e) => {
  //   e.preventDefault();
  //   ListsAPI.addList({list_name: this.state.list_name})
  //     .then(json => this.setState({
  //       list: json,
  //       redirect: true
  //     }))
  //     .catch(error => console.log(error))
  // }

  // handleListName = (e) => {
  //   this.setState({list_name: e.target.value});
  // }

  handleSubmit = (e) => {
    e.preventDefault();
    return this.state.users.map(user => {
      if(user.user_name === this.state.user_name)
      this.setState({
        user_name: user,
        redirect: true
      })
    })
  }

  handleUserName = (e) => {
    this.setState({user_name: e.target.value});
  }

  render() {
    if(this.state.redirect) {
      return <Redirect to={`/babynames/${this.state.user_name.id}`} />
    }

    return (
      <div>
        <form>
          <h6>Create New Baby Name List:</h6>
          <input type='text' value={this.state.list_name} onChange={this.handleListName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
        <form>
          <h6>Enter User Name:</h6>
          <input type='text' value={this.state.user} onChange={this.handleUserName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
      </div>
    )
  }
}

export default HomePage;
