import React, { Component } from 'react';
import ListsAPI from '../../api/ListsAPI';
import NamesAPI from '../../api/NamesAPI';
import '../../App.css';
import '../../pages/ListNamesPage.css'

class NameForm extends Component {

  checkBabyName = () => {
    if(this.props.names.length === 0) {
      this.props.handleNameSave();
    } else {
      let counter = 0;
      while(counter < this.props.names.length) {
        if(this.props.names[counter].baby_name.toUpperCase() === this.props.babyName.toUpperCase()) {
          this.props.saveBabyName();
          break;
        } else if(counter === this.props.names.length - 1) {
          this.props.handleNameSave();
        } 
        counter++;
      }
    }
  }

  handleBabyName = (e) => {
    this.props.handleBabyName(e);
  }

  handleSubmit = (e) => {
    e.preventDefault();
    if(/[0-9]+/gi.test(this.props.baby_name) === true) {
      return this.props.voidBabyName();
    }
    this.checkBabyName();
  }

  render() {
    return (
      <div>

        <form>
          <h5>Add Baby Name:</h5>
          <input type='text' value={this.props.baby_name} onChange={this.handleBabyName} />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
          { this.state.name_params === false ? <h6>Please do not include numbers in your names.</h6>: null }
          { this.state.redirect === true ? <h6>Name already included in list.</h6>: null }
        </form>
      </div>
    )
  }
}

export default NameForm;
