  componentDidMount() {
    ListsAPI.fetchListByID(this.props.match.params.listID)
    .then(json => this.setState({
      lists: json
    }))
  }

  handleSubmit = (e) => {
    e.preventDefault();
    ListsAPI.addList({list_name: this.state.list_name})
      .then(json => this.setState({
        list: json,
        redirect: true
      }))
      .catch(error => console.log(error))
  }

  handleListName = (e) => {
    this.setState({list_name: e.target.value});
  }

  // displayNames = () => {
