import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import NamesAPI from '../api/NamesAPI';
import ListsAPI from '../api/ListsAPI';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class ListPage extends Component {
  state = {
    list: null,
    names: null,
    baby_name: '',
    redirect: null,
    name_params: null
  }

  componentDidMount() {
    UsersAPI.updateUserData(this.props.match.params.userID, {recently_viewed_list: this.props.match.params.listID})
    ListsAPI.fetchListByID(this.props.match.params.listID)
    .then(json => this.setState({
      list: json
    }))
    NamesAPI.fetchNamesByListID(this.props.match.params.listID)
    .then(json => this.setState({
      names: json
    }))
  }

  displayNames = () => {
    let sortedNames = [];
    this.state.names.map(name => {
      name.baby_name.toUpperCase();
      sortedNames.push(name);
    })
    console.log(sortedNames)
    sortedNames.sort((a, b) => {
      let A = a.baby_name.toUpperCase();
      let B = b.baby_name.toUpperCase();
      return (A < B) ? -1: (A > B) ? 1: 0;
    });
    console.log(sortedNames)
      // if(name[index].baby_name.toUpperCase() < name[index += 1].baby_name.toUpperCase()) {
      //   return -1;
      // } else if(name[index].baby_name.toUpperCase() > name[index += 1].baby_name.toUpperCase()) {
      //   return 1;
      // } else {
      //   return 0;
      // }
    //   return (
    //     <div key={index}>
    //       {name.baby_name}<br/>
    //     </div>
    //   )}
    // )
  }

  checkBabyName = () => {
    let counter = 0;
    while(counter < this.state.names.length) {
      if(this.state.names[counter].baby_name === this.state.baby_name) {
        this.setState({ 
          baby_name: '',
          redirect: false
        })
        break;
      } else if(counter === this.state.names.length - 1) {
        NamesAPI.addListName(this.state.list.id, {baby_name: this.state.baby_name})
        .then(() => NamesAPI.fetchNamesByListID(this.state.list.id))
        .then(json => {
          this.setState({
            names: json,
            baby_name: '',
            redirect: true
        })})
      } 
      counter++;
    }
  }

  handleBabyName = (e) => {
    this.setState({baby_name: e.target.value});
  }

  handleSubmit = (e) => {
    e.preventDefault();
    if(/^[0-9]+$/gi.test(`${this.state.baby_name}`) === true) {
      return this.setState({
        baby_name: '',
        name_params: false
      })
    }
    this.checkBabyName();
  }

    // deleteThis = (componentID) => 
    //   Components.deleteComponent(componentID)
    //   .then(() => Components.fetchComponents())
    //   .then(json => {
    //     this.setState({
    //     components: json
    //   })
    // });

  render() {
    // if(this.state.redirect) {
    //   return this.state.names.map((name, index) => {
    //     return (
    //       <div key={index}>
    //         {name.baby_name}<br/>
    //       </div>
    //     )}
    //   )
    // }

    return (
      <div>
        { this.state.list ? <h2>{this.state.list.list_name}</h2>: null }
        <form>
          <h6>Add Baby Name:</h6>
          { this.state.name_params === false ? <h6>Please do not include numbers in your names.</h6>: null }
          <input type='text' value={this.state.baby_name} onChange={this.handleBabyName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
        {this.state.redirect ? this.displayNames(): this.state.names ? this.displayNames(): null}
      </div>
    )
  }
}

export default ListPage;
