import React, { Component } from 'react';
import ListsAPI from '../../api/ListsAPI';
import NamesAPI from '../../api/NamesAPI';
import '../../App.css';
import '../../pages/ListNamesPage.css'

class NamesDisplay extends Component {
  state = {
    displayDelay: null
  }

  componentDidMount() {
    if(this.props.names.length) {
      this.setState({ displayDelay: true })
    }
  }

  displayNames = () => {
    let sortedNames = [];
    this.props.names.map(name => {
      name.baby_name = name.baby_name.toUpperCase();
      sortedNames.push(name);
    })
    sortedNames.sort((a, b) => {
      let A = a.baby_name;
      let B = b.baby_name;
      return (A < B) ? -1: (A > B) ? 1: 0;
    });
    return sortedNames.map((name, index) => {
      return (
        <div key={index} className='baby-name'>
          {name.baby_name.toLowerCase()}<br/>
        </div>
      )
    })
  }

  render() {
    console.log(this.props.names, this.state.displayDelay)
    if(this.state.displayDelay) {
      return this.displayNames();
    }else if(this.props.redirect) {
      return this.displayNames();
    }

    return (
      <div>
      </div>
    )
  }
}

export default NamesDisplay;
