import React, { Component } from 'react';
import ListsAPI from '../../api/ListsAPI';
import NamesAPI from '../../api/NamesAPI';
import '../../App.css';
import '../../pages/ListNamesPage.css'

class NamesDisplay extends Component {
  state = {
    sortedNames: []
  }

  handleSort = () => {
    console.log(this.state.sortedNames)
  this.state.sortedNames.sort((a, b) => {
    let A = a.baby_name;
    let B = b.baby_name;
    return (A < B) ? -1: (A > B) ? 1: 0;
  });
  return this.state.sortedNames.map((name, index) => {
    return (
      <div key={index} className='baby-name'>
        {name.baby_name.toLowerCase()}<br/>
      </div>
    )
  })
}

  displayNames = () => {
    let sortedNames = [];
    this.props.names.map(name => {
      sortedNames.push(name.baby_name.toUpperCase());
    })
    this.setState({ sortedNames: sortedNames })
    this.handleSort();
  }

  render() {

    return (
      <div>
        {this.displayNames()}
      </div>
    )
  }
}

export default NamesDisplay;
