import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class HomePage extends Component {
  state = {
    users: null,
    user_name: null,
    user: '',
    redirect: false
  }

  componentDidMount() {
    UsersAPI.fetchUsers()
    .then(json => this.setState({
      users: json
    }))
  }

  handleSubmit = (e) => {
    e.preventDefault();
    let findUser = this.state.users.map(user => {
      if(user.user_name === this.state.user_name) {
        this.setState({ 
          user: user,
          redirect: true 
        })
      } else {
        this.setState({ 
          user: ''
        })        
      }
    })
      return findUser;
    }

  handleUserName = (e) => {
    this.setState({user_name: e.target.value});
  }

  render() {
    if(this.state.redirect) {
      return <Redirect to={`/babynames/${this.state.user.id}`} />
    }
    return (
      <div>
        <form>
          <h6>Enter User Name:</h6>
          <input type='text' value={this.state.user} onChange={this.handleUserName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
      </div>
    )
  }
}

export default HomePage;
