import React, { Component } from 'react';
import NamesDisplay from '../NamesDisplay/NamesDisplay';
import NameForm from '../NameForm/NameForm';
import ListsAPI from '../../api/ListsAPI';
import NamesAPI from '../../api/NamesAPI';
import '../../App.css';
import '../../pages/ListNamesPage.css'

class ListNamesPage extends Component {
  state = {
    list: null,
    names: null,
    baby_name: '',
    redirect: null,
    name_params: null
  }

  componentDidMount() {
    return this.props.lists.map(list => {
      if(list.list_name === this.props.listID)
      ListsAPI.fetchListByID(list.id)
      .then(json => this.setState({ list: json }))
      NamesAPI.fetchNamesByListID(list.id)
      .then(json => this.setState({ names: json }))
    })
  } 

  handleNameSave = () => {
    NamesAPI.addListName(this.state.list.id, {baby_name: this.state.baby_name})
    .then(() => NamesAPI.fetchNamesByListID(this.state.list.id))
    .then(json => {
      this.setState({
        names: json,
        baby_name: '',
        redirect: null,
        name_params: null,
    })})
  }

  saveBabyName = () => {
    this.setState({
      baby_name: '',
      redirect: true
    })
  }

  voidBabyName = () => {
    this.setState({
      baby_name: '',
      name_params: false
    })
  }

  handleBabyName = (e) => {
    this.setState({baby_name: e.target.value});
  }

  render() {
    console.log(this.state.names)
    return (
      <div>
        { this.state.list ? <h1>*{this.state.list.list_name}*</h1>: null }

        <div>
          <NameForm handleNameSave={this.handleNameSave} saveBabyName={this.saveBabyName} voidBabyName={this.voidBabyName} handleBabyName={this.handleBabyName} names={this.state.names} babyName={this.state.baby_name} redirect={this.state.redirect} nameParams={this.state.name_params} />
          { this.state.name_params === false ? <h6>Please do not include numbers in your names.</h6>: null }
          { this.state.redirect === true ? <h6>Name already included in list.</h6>: null }
        </div>

        <div className='baby-names'>
          {/* { this.state.names ? <NamesDisplay names={this.state.names}/>: this.state.redirect ? <NamesDisplay names={this.state.names}/>: null } */}
        </div>
      </div>
    )
  }
}

export default ListNamesPage;
