import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import NamesAPI from '../api/NamesAPI';
import ListsAPI from '../api/ListsAPI';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class ListPage extends Component {
  state = {
    list: null,
    names: null,
    baby_name: '',
    redirect: null
  }

  componentDidMount() {
    UsersAPI.updateUserData(this.props.match.params.userID, {recently_viewed_list: this.props.match.params.listID})
    ListsAPI.fetchListByID(this.props.match.params.listID)
    .then(json => this.setState({
      list: json
    }))
    NamesAPI.fetchNamesByListID(this.props.match.params.listID)
    .then(json => {this.setState({
      names: json
    })
  console.log(json)
  })
  }

  // displayNames = () => {
  //   let name = this.state.names.map((name, index) => {
  //     return <p key={index}>{name.baby_name}</p>
  //   })
  //   return name;
  // }

  checkBabyName = () => {
    console.log('CHECKBABY', this.state.names)
    let counter = 0;
    while(counter < this.state.names.length) {
      if(this.state.names[counter].baby_name === this.state.baby_name) {
        this.setState({ 
          baby_name: '',
          redirect: false
        })
        break;
      } else if(counter === this.state.names.length - 1) {
        console.log(this.state.baby_name)
        NamesAPI.addListName(this.state.list.id, {baby_name: this.state.baby_name})
        .then(() => NamesAPI.fetchNamesByListID(this.state.list.id))
        .then(json => {
          this.setState({
            names: json,
            baby_name: '',
            redirect: true
        })
      console.log(json)
      })
      } 
      counter++;
    }
  }

  handleBabyName = (e) => {
    console.log(this.state.baby_name)
    this.setState({baby_name: e.target.value});
  }

  handleSubmit = (e) => {
    console.log('SUBMIT')
    e.preventDefault();
    this.checkBabyName();
  }

    // deleteThis = (componentID) => 
    //   Components.deleteComponent(componentID)
    //   .then(() => Components.fetchComponents())
    //   .then(json => {
    //     this.setState({
    //     components: json
    //   })
    // });

  render() {
    if(this.state.redirect) {
      console.log('REDIRECT')
      return this.state.names.map((name, index) => {
        return (
          <div key={index}>
            {name.baby_name}<br/>
          </div>
        )}
      )
    }

    return (
      <div>
        { this.state.list ? <h2>{this.state.list.list_name}</h2>: null }
        <form>
          <h6>Add Baby Name:</h6>
          <input type='text' value={this.state.baby_name} onChange={this.handleBabyName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
        {/* {this.state.names ? this.displayNames(): null} */}
      </div>
    )
  }
}

export default ListPage;
