import React, { Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import NamesAPI from '../api/NamesAPI';
import ListsAPI from '../api/ListsAPI';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class ListNamesPage extends Component {
  state = {
    names: null,
    user: null,
    list_teaser: null,
    lists: null
  }

  componentDidMount() {
    NamesAPI.fetchNamesByListID(this.props.match.params.listID)
    .then(json => this.setState({
      names: json
    }))
    UsersAPI.fetchUserByID(this.props.match.params.userID)
    .then((json) => ListsAPI.fetchListByID(json.recently_viewed_list))
    .then(json => this.setState({
      list_teaser: json
    }))
    ListsAPI.fetchLists()
    .then(json => this.setState({
      lists: json
    }))
  }

  handleAllListDisplays = () => {
    return this.state.lists.map((list, index) => {
      if(this.state.list_teaser.list_name !== list.list_name) {
      return (
        <div key={index}>
        <Link to={`/babynames/${this.props.match.params.userID}/lists/${list.id}`}>{list.list_name}</Link><br/>
        </div>
      )}
    })
  }

//   Settings.deleteComponentSetting(componentID, settingID)
//   .then(() => Settings.fetchSettingsByComponentID(this.props.match.params.components))
//   .then(json => {
//     this.setState({
//     settings: json
//   })
// });

  render() {
    console.log(this.state.list_teaser)
    return (
      <div>
        <h3>Recently Viewed List:</h3>
        <div>
          { this.state.list_teaser ? <Link to={`/babynames/${this.props.match.params.userID}/lists/${this.state.list_teaser.id}`}>{this.state.list_teaser.list_name}</Link>: null }
        </div>
        { this.state.lists && this.state.list_teaser ? this.handleAllListDisplays(): null }
      </div>
    )
  }
}

export default ListNamesPage;
