import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import NamesAPI from '../api/NamesAPI';
import ListsAPI from '../api/ListsAPI';
import '../App.css';

class UserPage extends Component {
  state = {
    lists: null,
    list: null,
    list_name: '',
    redirect: false
  }

  componentDidMount() {
    ListsAPI.fetchLists()
    .then(json => this.setState({
      lists: json
    }))
  }

  checkListName = () => {
    let counter = 0;
    while(counter < this.state.lists.length) {
      if(this.state.lists[counter].list_name === this.state.list_name) {
        this.setState({
          list: this.state.users[counter], 
          redirect: true 
        });
        break;
      } else if(counter === this.state.users.length - 1) {
        this.setState({
          user_name: '',
          redirect: false
        })
      }
      counter++;
    }
  }

handleSubmit = (e) => {
  e.preventDefault();
  this.checkName();
}

  handleSubmit = (e) => {
    e.preventDefault();
    ListsAPI.addList({list_name: this.state.list_name})
      .then(json => this.setState({
        list: json,
        redirect: true
      }))
  }

  handleListName = (e) => {
    this.setState({list_name: e.target.value});
  }

  render() {
    if(this.state.redirect) {
      return <Redirect to={`/babynames/${this.props.match.params.userID}/${this.state.list.id}`} />
    }

    return (
      <div>
        <form>
          <h6>Create New Baby Name List:</h6>
          <input type='text' value={this.state.list_name} onChange={this.handleListName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
        {/* {this.state.names ? this.displayNames(): null} */}
      </div>
    )
  }
}

export default UserPage;
