import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import UsersAPI from '../api/UsersAPI';
import '../App.css';

class HomePage extends Component {
  state = {
    users: null,
    user_name: null,
    user: null,
    redirect: false
  }

  componentDidMount() {
    UsersAPI.fetchUsers()
    .then(json => this.setState({
      users: json
    }))
  }

  redirectFunction = (userID) => {
    console.log(userID)
    return <Redirect to={`/babynames/${userID}`} />
  }

  checkName = () => {
    return this.state.users.map(user => {
      let holder = false;
      if(user.user_name === this.state.user_name) {
        holder = true;
        if(holder === true) {
          return this.redirectFunction(user.id)
        }
      } else {
        holder = false;        
      }
    })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.checkName();
  }

  handleUserName = (e) => {
    this.setState({user_name: e.target.value});
  }

  render() {
    return (
      <div>
        <form>
          <h6>Enter User Name:</h6>
          <input type='text' value={this.state.user} onChange={this.handleUserName} /><br />
          <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
      </div>
    )
  }
}

export default HomePage;
